// create a area circle for sun 
float circle(in vec2 p, in vec2 c, in float r)
{
    // math formula for circle 
    return abs(r - length(p - c));
}
// create a scene for sun 
float scene(vec2 uv)
{    
    // set data 
    vec2 c = vec2(.01);
    // create the sun with clamp and circle formula
    float c1 = clamp(circle(1.-uv, c, 1.),0.1,0.9);
    // return c1 ( all data) 
    return c1;
}
// default mainImage to show on display
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	// set uv area 
    vec2 uv = (fragCoord.xy/iResolution.xy);
    // set color
  	vec3 col = vec3(0.1, 0.5, 100);
    // normalized pixel coordinates (from 0 to 1)
    uv = fragCoord/iResolution.xy;
	// show scene with cerc
    col += scene(uv);
    // output all to screen with fragColor
    fragColor = vec4(col,0);
}
