void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0 - iResolution.xy)/ min(iResolution.x, iResolution.y);

   vec2 st = uv;
   st = fract(st);
    vec2 id = floor(uv);
    st.x -= 0.5;
    //st.y -= 0.5;
    vec3 col = vec3(0.0);

    float offs = rand(id)*60.;
    float size = (sin(iTime*7. + offs))*0.2;


    float d = distance(st, vec2(0.0, 0.0));
    d = step(d, 0.8);

    vec3 cw = vec3(1.0);
    vec3 cb = vec3(0.0);

    float er = distance(st, vec2(0.2, 0.7));
    er = step(0.25, er);

    float el = distance(st, vec2(-0.2, 0.7));
    el = step(0.25, el);

    float erb = distance(st, vec2(0.3-abs(cos(iTime+0.4+offs)*0.2), 0.6+abs(sin(iTime+0.4+offs)*0.2)));
    erb = step(erb, 0.1);

    float elb = distance(st, vec2(-0.1-abs(cos(iTime+0.4+offs)*0.2), 0.6+abs(sin(iTime+0.4+offs)*0.2)));
    elb = step(elb, 0.1);    

    st.y -= 0.32;
    st = abs(st);
    float m = dot(st, normalize(vec2(0.4, 0.6)));
    m = max(m+0.755 ,st.y);
    m = step(m, 0.9);

    vec3 c1 = vec3(0.2, 0.4, 0.2);
    vec3 c2 = vec3(0.9, 0.9, 0.3);
    col = mix(c1, c2, d);
    col *= mix(cw, cb, erb);
    col *= mix(cw, cb, elb);
    col *= vec3(mix(cw, vec3(0.9, 0.5, 0.9), m));
    col /= mix(cb, cw, er);
    col /= mix(cb, cw, el);




    // Output to screen
    fragColor = vec4(col,1.0);
}
