const vec3 ChromaKey = vec3(0.051, 0.639, 0.145);

vec4 Main(vec2 uv)
{
    vec3 fg = texture(iChannel0, uv).rgb;
    float key = distance(fg, ChromaKey);

    vec3 bg = texture(iChannel1, uv).rgb;
    vec3 color = mix(bg, fg, smoothstep(0.1, 0.5, key));

    return vec4(color, 1);
}

const vec3 LumaCoeffs = vec3(0.299, 0.587, 0.114);
const float LumaRatio = 0.95;
const float ChromaRatio = 1.0 - LumaRatio;

const mat3x3 RgbToYiq = mat3x3(
    0.299, 0.587, 0.114,
    0.5959, -0.2746, -0.3213,
    0.2115, -0.5227, 0.3112);

const mat3x3 YiqToRgb = mat3x3(
    1.0, 0.956, 0.619,
    1.0, -0.272, -0.647,
    1.0, -1.106, 1.703);

#define iPixelSize vec2(1.0 / iResolution.xy)

vec4 Main(vec2 uv);

#define mainImage(color, coord) (color = Main(coord * iPixelSize))

#define Saturate(x) clamp(x, 0.0, 1.0)

#define ScaleCoord(uv, scale, center) (((uv) - (center)) * (scale) + (center))

vec4 Main(vec2 uv)
{
    vec2 imgUv;
    imgUv.x = (uv.x < LumaRatio)
        ? uv.x / LumaRatio
        : (uv.x - LumaRatio) / ChromaRatio;
    imgUv.y = uv.y;

    vec3 rgb = texture(iChannel0, imgUv).rgb;
    vec3 yiq = RgbToYiq * rgb;

    vec3 color = (uv.x < LumaRatio)
        ? vec3(yiq.x)
        : vec3(yiq.yz, 0);

    return vec4(color, 1);
}

vec4 Main(vec2 uv)
{
    vec2 yUv = vec2(uv.x * LumaRatio, uv.y);    
    float y = texture(iChannel0, yUv).x;

    vec2 iqUv = vec2(uv.x * ChromaRatio + LumaRatio, uv.y);
    vec2 iq = texture(iChannel0, iqUv).xy;

    vec3 rgb = YiqToRgb * vec3(y, iq);

    return vec4(rgb, 1);
}
