#define R iResolution;
#define S(left,right,x) smoothstep(left,right,x);

float rect(vec2 uv,float left,float right,float top,float bottom,float blur) {
    float c = 1.;

    c = S(left,left - blur,uv.x);
    c += S(right,right + blur,uv.x);
    c += S(top,top - blur,uv.y);
    c += S(bottom,bottom + blur,uv.y);
    return c;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = vec4(1.,1.,1.,0.001);

    uv -= 0.5;
    //uv *= 2.;

    uv.x *= iResolution.x / iResolution.y;

    uv.y += 5. * -tan(0.4 * (uv.x / 2.) + iTime);

    float c = rect(uv,-1.,1.,-0.325,0.0,0.05);

    vec4 res = vec4(vec3(1.- c),c);

    col *= res;

    fragColor = col;


}
