void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(mod(uv,sound(iTime+uv.x)),1.0,1.0);
}

vec2 sound1(int t)
{
    //t += ((t/10))*10;
    t = (t/(2))|(t/4)/((t*2)%16)|t/512;
    return vec2(float(t & 0xff - 128) / 128.);
}

vec2 sound(float time){
    time *= 500.0;
    float factor = 1.0;
    vec2 result = vec2(0.0);
    for(int i = 0; i < 3; i++){
    	result += sound1(int(time*factor))*factor;
        factor *= 1.5;
    }
    return result/10.0;
}

// http://countercomplex.blogspot.jp/2011/10/algorithmic-symphonies-from-one-line-of.html
