void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x += sin(iTime+uv.y)/3.33;
    uv.y += cos(iTime+uv.x)/1.77;

    float x = abs(sin(24.0*uv.x));
    float y = abs(cos(24.0*uv.y));
    vec3 color = vec3(1.0);

    color *= x*y;
    color.r *= uv.x;
    color.g *= uv.y;
    fragColor = vec4(color,1.0);

}
