vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*1103515245U;
    x = ((x>>8U)^x.yzx)*1103515245U;
    x = ((x>>8U)^x.yzx)*1103515245U;

    return vec3(x)*(1.0/float(0xffffffffU));
}

float random (in vec2 st) {

    uvec3 ust = uvec3(st.x, st.y, st.x * st.y);
    vec3 r = hash(ust);
    return r.x;

