vec3 blackground = vec3(0.0,0.0,0.0);
vec3 yellow = vec3(1.0,1.0,0.0);
vec3 red = vec3(1.0,0.0,0.0);
vec3 blue = vec3(0.0,1.0,0.0);

float circle(vec2 uv, vec2 pos, float r)
{       
    float dist = distance(pos, uv);
    float value = step(r, dist);

    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 mouse = iMouse.xyz/iResolution.xyz;

    uv.x *= iResolution.x/iResolution.y;

    float a = 0.33;
    float b = 0.67;
    float c = 0.0;

    // 1- mask
    float valueA = step(c,uv.y)-step(a, uv.y);
    float valueB = step(a,uv.y) - step(b,uv.y);

    vec3 color = mix(blue, red, valueA);
    color = mix(color, yellow, valueB);

    float value = circle(uv,vec2(0.9,0.67), 0.08);
    vec3 image = mix(vec3(value), color, value);

    float value2 = circle(uv,vec2(0.9,0.509), 0.08);
    image = mix(image, vec3(value2), image);

    float value3 = circle(uv,vec2(0.9,0.35), 0.08);
    image = mix(image, vec3(value3), image);

    vec3 cirCol;
    if(mouse.y >= 0.67)
    {
      cirCol = mix(blue * fract(iTime * 2.0), blackground, value);
    }
    else if (mouse.y <= 0.35)
    {
      cirCol = mix(red * fract(iTime * 2.0), blackground, value3);
    }
    else
    {
      cirCol = mix(yellow * fract(iTime * 2.0), blackground, value2);
    }

    image = mix(cirCol, image, image);

    // Output to screen
    fragColor = vec4(image,1.0);
}
