void mainImage(out vec4 O, vec2 C) {
    O=vec4(0);
	for(float n,g,e,j,i;i++<50.;){
    	vec3 p=g*vec3((C*2.-iResolution.xy)/iResolution.y,1);
        p.z+=iTime;
        n=ceil(p.z);
        p=fract(p)-.5;
        for(j=0.;j++<3.+mod(n,2.);)
            p.xy=abs(p.xy)-.05,
            p.xy=vec2(p.x+p.y,p.x-p.y)*.7;
        g+=e=.5*length(p.yz)-.001;
        e<.01?O.xyz+=abs(sin(vec3(1,2,3)+n))*.6/i:p;
    }
}
