// It would be shorter... :)

#define M(p) = (.2- smoothstep (0.2, .5, length(U)))* \
 fract( atan(U.x,U.y) / 6.28 -p ),
 void mainImage( out vec4 O, vec2 u )
{
   vec2 U = (.6*u-.3*iResolution.xy)/iResolution.y;
   float
    i=iDate.w/60.,
    s M(i)
    m M(i/60.)
    h M(i/7.2e2)  
    b = length(U);
  O = vec4(h,m,s,.0)/b;   
}

