/** // 150 chars

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, U = (u+u-R)/R.y;
    float t = iTime,                             //  / 60. if you want minutes
    a = fract( atan(U.x,U.y) / 6.2832 -t ) + t;
    O = .6 + .6 * cos( a + vec4(0,23,21,0)  );
}

