#define blue  vec3(0.415, 0.445, 1.000)
#define white vec3(1.)

void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    float ratio = iResolution.x / iResolution.y;
    float offset = (ratio - 1.) * 0.5;

    vec2 uv = fragCoord / iResolution.xy;
	uv.x = uv.x * ratio - offset;

    vec2 m = iMouse.xy / iResolution.xy;
    m.x = m.x * ratio - offset;

    float size = 0.05;
    float ellipse = 1. - smoothstep(size, size + 0.01, distance(uv, m) * length(uv - 0.5));

    vec3 background = 1. - ellipse * white;
    vec3 col = ellipse * blue + background;

    fragColor = vec4(col, 1.0);
}
