void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    int tX = int(fragCoord.x) / 4;
    int tY = int(fragCoord.y) / 3;
   	vec2 uv = vec2(float(tX * 4), float(tY * 3)) / iResolution.xy;
  	vec4 c = texture(iChannel0, uv);
    if ((int(fragCoord.x) % 4) == 3) {
    	fragColor = vec4(0,0,0,1.0);
    } else {
        int rgb = int(fragCoord.y) % 3;
        if (rgb == 0) {
            fragColor = vec4(c.x*1.3, 0, 0, 1.0);
        } else if (rgb == 1) {
        	fragColor = vec4(0, c.y*1.3, 0, 1.0);
        } else {
            fragColor = vec4(0, 0, c.z*1.3, 1.0);
        }
    }
}
