#define CS(a) vec2(cos(a),sin(a))
#define OCTAVES 5

float random(in vec2 st)
{
	return fract(sin(dot(st.xy,vec2(12.443,78.4)))*43758.2);
}

float noise(in vec2 st)
{
	vec2 i=floor(st);
    vec2 f=fract(st);
    float a=random(i);
    float b=random(i+vec2(1.0,0.0));
    float c=random(i+vec2(0.0,1.0));
    float d=random(i+vec2(1.0,1.0));

    vec2 u=smoothstep(0.0,1.0,f);
    return mix(a,b,u.x)+(c-a)*u.y*(1.0-u.x)+(d-b)*u.x*u.y;

}

float fbm(vec2 p)
{
	float result=0.0;
    float amplitude=1.0;

    for(int i=0;i<OCTAVES;i++)
    {
    	result+=amplitude*noise(p);
        amplitude*=0.5;
        p*=2.;
    }
    return result;
}


float orb(vec2 st,float s,float a)
{
	st+=a*CS(iTime*s);
   	st.x+=1.5*sin(iTime);
    float value=length(st);
    float c=smoothstep(0.1,0.11,value);
	return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p=(fragCoord.xy*2.0-iResolution.xy)/iResolution.y;    
    p+=fbm(p);
    float col=1.0;

    col*=orb(p,0.,0.);

    for(float i=1.;i<=8.0;i++)
    {

    	col*=orb(p,0.1*i,0.24*i);
    }

    fragColor=vec4(1.-col);

}
