float hash(float n) { return fract(sin(n) * 43758.5453123); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float time = iTime * 1.0;

    float s = abs(sin(time));
    uv.x += hash(uv.y * time) * s * 0.01;

    //vec4 col = texture(iChannel0, uv);

    vec2 uv1 = uv.xy + vec2(0.0) * s;
    vec2 uv2 = uv.xy + vec2(0.0, 0.02) * s;
    vec2 uv3 = uv.xy + vec2(0.0, 0.03) * s;

    float r = texture(iChannel0, uv1).r;
    float g = texture(iChannel0, uv2).g;
    float b = texture(iChannel0, uv3).b;

    vec3 col = vec3(r, g, b);

    fragColor = vec4(col.rgb,1.0);
}
