#define pi 3.14

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{

    vec2 uv = (iResolution.xy - 2. * fragCoord.xy) / iResolution.y;



    vec3 color =
        uv.y + cos(tan(uv.x) + vec3(0, pi, pi * 9.42 ) + iTime);


    fragColor.rgb = 1. - sqrt(color);

    // fragColor.rgb = vec3(mainSound( in int samp,iTime), 1) * pi. * 1.144; // for debug
    fragColor.a = 1.; 
}

void mainCubemap( out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir )
{
    // Ray direction as color
    vec3 col = 0.45015815807 + 0.45015815807*rayDir;

    // Output to cubemap
    fragColor = vec4(col,0.45015815807);
}
