// copy from https://www.shadertoy.com/view/4sc3z2

#define MOD3 vec3(.1031,.11369,.13787)

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * MOD3);
    p3 += dot(p3, p3.yxz+19.19);
    return -1.0 + 2.0 * fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}
float perlin_noise(vec3 p)
{
    vec3 pi = floor(p);
    vec3 pf = p - pi;

    vec3 w = pf * pf * (3.0 - 2.0 * pf);

    return 	mix(
        		mix(
                	mix(dot(pf - vec3(0, 0, 0), hash33(pi + vec3(0, 0, 0))), 
                        dot(pf - vec3(1, 0, 0), hash33(pi + vec3(1, 0, 0))),
                       	w.x),
                	mix(dot(pf - vec3(0, 0, 1), hash33(pi + vec3(0, 0, 1))), 
                        dot(pf - vec3(1, 0, 1), hash33(pi + vec3(1, 0, 1))),
                       	w.x),
                	w.z),
        		mix(
                    mix(dot(pf - vec3(0, 1, 0), hash33(pi + vec3(0, 1, 0))), 
                        dot(pf - vec3(1, 1, 0), hash33(pi + vec3(1, 1, 0))),
                       	w.x),
                   	mix(dot(pf - vec3(0, 1, 1), hash33(pi + vec3(0, 1, 1))), 
                        dot(pf - vec3(1, 1, 1), hash33(pi + vec3(1, 1, 1))),
                       	w.x),
                	w.z),
    			w.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float dis1 = (perlin_noise(vec3(fragCoord.xy/iResolution.xy, iTime*0.001)*256.0));
    float dis2 = (perlin_noise(vec3(fragCoord.xy/iResolution.xy, iTime*0.005)*128.0));
    float dis3 = (perlin_noise(vec3(fragCoord.xy/iResolution.xy, iTime*0.005)*64.0));
    float dis4 = (perlin_noise(vec3(fragCoord.xy/iResolution.xy, iTime*0.005)*16.0));
    float dis5 = (perlin_noise(vec3(fragCoord.xy/iResolution.xy, iTime*0.05)*2.0));
    vec3 color = vec3(sin(dis1*0.1 + dis2*0.1 + dis3*0.2 + dis4*0.5 + dis5*0.8 + 0.2)) * 2.0;
	fragColor = vec4(color.xyz,1.0);
}
