#define R iResolution.xy
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
void mainImage( out vec4 Q, in vec2 U )
{
      vec4
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    vec2 g = vec2(e.z-w.z,n.z-s.z);
    vec4 a = A(U);

    Q = abs(sin(a.w*4.+2.*a.z*vec4(0.1,1.4,1.6,1.4)));
	Q *= 1.-texture(iChannel1,normalize(vec3(g,.5)));
}

#define R iResolution.xy
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 n = 
        A(U+vec2(0,1))+
        A(U+vec2(1,0))+
        A(U+vec2(0,-1))+
        A(U+vec2(-1,0))+
        A(U+vec2(-1,1))+
        A(U+vec2(1,-1))+
        A(U+vec2(-1,-1))+
        A(U+vec2(1,1));
    vec4 dx = 0.125*(n)-Q;
    Q.zw += dx.zw*vec2(sin(iTime/6.0+7.)*2.5,.2);
    float x = 2.36*Q.z*Q.w*(1.-Q.w);
    Q.w = Q.w+x-0.027;
    Q.z = Q.z+0.1*Q.z*(1.-Q.z)-x;

    Q.zw = clamp(Q.zw,0.,1.);

    if (iMouse.z> 0. && length(U-iMouse.xy) < 20.) Q.w=1.;

    if (iFrame < 1) {

        Q = vec4(0,0,1,0);
        if (length(U-0.5*R)<5.) Q.w = 1.5;
    }
}

#define R iResolution.xy
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 n = 
        A(U+vec2(0,1))+
        A(U+vec2(1,0))+
        A(U+vec2(0,-1))+
        A(U+vec2(-1,0))+
        A(U+vec2(-1,1))+
        A(U+vec2(1,-1))+
        A(U+vec2(-1,-1))+
        A(U+vec2(1,1));
    vec4 dx = 0.125*(n)-Q;
    Q.zw += dx.zw*vec2(1,.2);
    float x = .36*Q.z*Q.w*(1.-Q.w);
    Q.w = Q.w+x-0.027;
    Q.z = Q.z+0.1*Q.z*(1.-Q.z)-x;

    Q.zw = max(Q.zw,0.);

    if (iMouse.z> 0. && length(U-iMouse.xy) < 20.) Q.w=1.;

    if (iFrame < 1) {

        Q = vec4(0,0,1,0);
        if (length(U-0.5*R)<5.) Q.w = 1.;
    }
}
