const float FOVY = 3.14159 * 0.25;
const int RAY_STEPS = 256;
const float PI = 3.14159;
const float TWO_PI = 3.14159 * 2.0;
const vec3 matteWhite = vec3(0.85, 0.81, 0.78);
const vec3 matteRed = vec3(0.63, 0.065, 0.05);
const vec3 matteGreen = vec3(0.14, 0.45, 0.091);
const float SHADOW_HARDNESS = 6.0;

// Adjust these to alter where the subsurface glow shines through and how brightly
const float K_VAL = 2.0;
const float OCC_DIST = 0.085;
// The larger the DISTORTION, the smaller the glow
const float DISTORTION = 0.2;
// The higher GLOW is, the smaller the glow of the subsurface scattering
const float GLOW = 6.0;
// The higher the BSSRDF_SCALE, the brighter the scattered light
const float BSSRDF_SCALE = 3.0;
// Boost the shadowed areas in the subsurface glow with this
const float AMBIENT = 0.0;

//Intersection
struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object; // unique ID for every object
};    

// Box with side lengths b
float cube(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}

// SquarePlane SDF
float plane(vec3 p, vec4 n)
{
    n = normalize(n);
    return dot(p, n.xyz) + n.w;
}

// Sphere with radius r at center c
float sphere(vec3 p, float r, vec3 c)
{
    return distance(p, c) - r;
}

#define BACK_WALL 0
#define LEFT_WALL 1
#define RIGHT_WALL 2
#define FLOOR 3
#define SPHERE1 4
#define SHORT_CUBE 5
#define BIG_CUBE 6
#define LIGHT 7
#define BACK_WALL_SDF plane(pos, vec4(0.0, 0.0, -1.0, 15.0))
#define LEFT_WALL_SDF plane(pos, vec4(1.0, 0.0, 0.0, 5.0))
#define RIGHT_WALL_SDF plane(pos, vec4(-1.0, 0.0, 0.0, 5.0))
#define CEILING_SDF plane(pos, vec4(0.0, -1.0, 0.0, 7.5))
#define FLOOR_SDF plane(pos, vec4(0.0, 1.0, 0.0, 4.0))
#define SPHERE1_SDF sphere(rotateY(pos, 15.0 * 3.14159 / 180.0), 4.5, vec3(-7.0, 3.0, -1.0))
#define SHORT_CUBE_SDF cube(rotateY(pos + vec3(-2, 1.5, -0.75), -17.5 * 3.14159 / 180.0), vec3(2.5, 8.0, 2.5))

vec3 rotateY(vec3 p, float a)
{
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);   
}

float shadowMap3D(vec3 pos)
{
    float t = FLOOR_SDF;

    float t2;
    if ((t2 = SPHERE1_SDF) < t) {
        t = t2;
    }
    if ((t2 = SHORT_CUBE_SDF) < t) {
        t = t2;
    }
    return t;
}

// function to create whole scene
void sceneMap3D(vec3 pos, out float t, out int obj, vec3 lightPos)
{
    t = FLOOR_SDF;
    obj = FLOOR;

    float t2;
    if ((t2 = SPHERE1_SDF) < t) {
        t = t2;
        obj = SPHERE1;
    }
    if ((t2 = SHORT_CUBE_SDF) < t) {
        t = t2;
        obj = SHORT_CUBE;
    }
    if((t2 = sphere(pos, 2.0, lightPos)) < t) {
        t = t2;
        obj = LIGHT;
    }
}

float sceneMap3D(vec3 pos, vec3 lightPos)
{
    float t = sphere(pos, 2.0, lightPos);

    float t2;
    if ((t2 = FLOOR_SDF) < t) {
        t = t2;
    }
    if ((t2 = SPHERE1_SDF) < t) {
        t = t2;
    }
    if ((t2 = SHORT_CUBE_SDF) < t) {
        t = t2;
    }
    return t;
}

float fiveTapAO(vec3 p, vec3 n, float k) {
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i) {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * OCC_DIST - shadowMap3D(p + n * i * OCC_DIST));
    }
    return 1.0 - k * aoSum;
}

float softShadow(vec3 dir, vec3 origin, float min_t, float k) {
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = shadowMap3D(origin + t * dir);
        if(m < 0.0001) {
            return 0.0;
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}

float subsurface(vec3 lightDir, vec3 normal, vec3 viewVec, float thickness) {
    vec3 scatteredLightDir = lightDir + normal * DISTORTION;
    float lightReachingEye = pow(clamp(dot(viewVec, -scatteredLightDir), 0.0, 1.0), GLOW) * BSSRDF_SCALE;
    float attenuation = 1.0;
    attenuation = max(0.0, dot(normal, lightDir) + dot(viewVec, -lightDir));
    float totalLight = attenuation * (lightReachingEye + AMBIENT) * thickness;
    return totalLight;
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos)
{
    t = 0.001;
    for (int i = 0; i < RAY_STEPS; ++i) {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj, lightPos);
        if (m < 0.001) {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightVec, vec3 view, float thick)
{
    float t;
    switch(hitObj) {
        case FLOOR:
        return matteWhite * max(0.0, dot(n, lightVec)) * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        break;
        case SPHERE1:
        vec3 sColor = vec3(0.85, 0.41, 0.2) * max(0.0, dot(n, lightVec)) * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        return sColor + vec3(0.85, 0.41, 0.2) * subsurface(lightVec, n, view, thick);
        break;
        case SHORT_CUBE:
        vec3 cColor = vec3(0.1, 0.31, 0.6) * max(0.0, dot(n, lightVec)) * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        return cColor + vec3(0.1, 0.31, 0.6) * subsurface(lightVec, n, view, thick);
        break;
        case LIGHT:
        return vec3(1.0, 0.88, 0.7);
        case -1:
        return vec3(0, 0, 0);
        break;
    }
    return vec3(0, 0, 0);
}

vec3 computeNormal(vec3 pos, vec3 lightPos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                         sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                         sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}

Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + (t * dir);
    vec3 nor = computeNormal(isect, lightPos);

    vec3 lightDir = normalize(lightPos - isect);
    float thickness = fiveTapAO(isect, -nor, K_VAL);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect), thickness);

    return Intersection(t, surfaceColor, isect, hitObj);
}

// Returns direction of ray
vec3 rayCast(vec3 eye, vec3 ref, vec2 ndc)
{
    vec3 F = ref - eye;
    vec3 R = normalize(cross(vec3(0, 1, 0), F));
    vec3 U = normalize(cross(R, -F));

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + ndc.x * H + ndc.y * V;

    return normalize(p - eye);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 lightPos = rotateY(vec3(-8.0, 7.0, -10), iTime * 0.6);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Convert range to [-1, 1]
    uv = uv * 2.0 - vec2(1.0, 1.0);

    vec3 eye = vec3(9, 2.5, -24);
    vec3 ref = vec3(-2, 2.5, 0);

    vec3 rayDir = rayCast(eye, ref, uv);

    Intersection isect = sdf3D(rayDir, eye, lightPos);

    fragColor = vec4(isect.color, 1.0);
}
