vec3 color(vec2 z) {
    bool fl = false;
    float w = 0.0;
    for(int i=0;i<60;i++) {
        w += z.y;
        w += abs(z.x);
        z.x = fract(z.x);
        if (z.x > 0.5) {
            z.x = 1.0-z.x;
            fl = !fl;
        }
        if (dot(z,z) < 1.0) {
            z /= dot(z,z);
            fl = !fl;
            continue;
        }

    }
    float c = 0.5 - 40.0 / w;
    if (fl) {
        c = 1.0 - c;
    }
    return vec3(c,c,c);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * (fragCoord - iResolution.xy * 0.5)/iResolution.y;

    float r2 = dot(uv,uv);
    if (r2 < 1.0) {
        uv.y -= 1.0;
        uv /= dot(uv,uv);
        uv.y = -0.5 - uv.y;

        float t = 0.1 * iTime;
        float period = 4.0 * log((1.0 + sqrt(5.0))*0.5);
        t = mod(t,period) - period * 0.5;
        uv *= exp(t);

        uv.x -= sqrt(0.2);
        uv /= dot(uv,uv);
        uv.x += 0.5 * (1.0 + sqrt(5.0));
        //uv.x += 0.1 * iTime;
        //uv.x = fract(uv.x);
        fragColor = vec4(color(uv),1.0);
    } else {
        fragColor = vec4(0.5,1.0,0.5,1.0);
    }
}
