vec3 visualizer1(vec2 uv){
	float f=floor((iTime*sampleRate)/256.)*256.,
          t=(uv.x*256.)+f,
          a=fbytebeat(t),
          b=float(int(bytebeat(int((uv.x*256.)+f)))^int(bytebeat(int((uv.y*256.)+f))))/255.;
    return vec3(float(uv.y<a),a,b);
}
vec3 visualizer2(vec2 uv){
    float s=256.;
    return vec3(fbytebeat((floor(uv.y*s)*s)+(uv.x*s)+(floor((iTime*sampleRate)/s)*s)));
}
void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec2 uv=fragCoord/iResolution.xy;
    fragColor=vec4(uv.x<.5?visualizer1(vec2(uv.x*2.,uv.y)):visualizer2(vec2((uv.x*2.)-1.,uv.y)),1.);
}

float sampleRate=8000.;
int bytebeat(int t){
    return
    ((t<<1)^((t<<1)+(t>>7)&t>>12))|t>>(4-(1^7&(t>>19)))|t>>7;//http://canonical.org/~kragen/bytebeat/
    //(t|(t>>9|t>>7))*t&(t>>11|t>>9);
}
float fbytebeat(float t){
    return mod(float(bytebeat(int(t))),256.)/255.;
}

float catmullRomInterpolation(float p0,float p1,float p2,float p3,float t){
    return((2.*p1)+((-p0+p2)*t)+((2.*p0-5.*p1+4.*p2-p3)*pow(t,2.))+((-p0+3.*p1-3.*p2+p3)*pow(t,3.)))/2.;
}
vec2 mainSound( in int samp,float time){
    float t=time*sampleRate,volume=.1;
    //return vec2(mix(fbytebeat(floor(t)),fbytebeat(floor(t)+1.),fract(t))*volume);
    return vec2(catmullRomInterpolation(fbytebeat(floor(t)-1.),fbytebeat(floor(t)),fbytebeat(floor(t)+1.),fbytebeat(floor(t)+2.),fract(t))*volume);
}
