void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float x = fragCoord.x;
    float y = iResolution.y - fragCoord.y - 1.0;
 	float t = iTime * -30.0;    
    fragColor = vec4(
          mod(x + mod(y + t, 64.0) + y + mod(x + t, 64.0), 256.0) / 256.0,
          0,
          mod(x + mod(y + t, 128.0) + y + mod(x + t, 128.0), 256.0) / 256.0,
        1.0
    );
}
