const float s3 = sqrt(3.);
const vec2 r = vec2(1, s3);
const vec2 h = r*.5;
const float zoom = 5.;

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float minRes = min(iResolution.x,iResolution.y);
    vec2 uv = (fragCoord-.5*iResolution.xy)/minRes*zoom;
    float eps    = zoom/minRes;

    vec2 a = mod(uv, r)-h;
    vec2 b = mod(uv-h, r)-h;

    vec2 c = dot(a,a) < dot(b,b) ? a : b;

    vec3 col = vec3(0);

    col   += smoothstep(0.10, 0.05, length(c));
    col.r += smoothstep(0.02, 0.01, abs(a.x)) * 0.2;
    col.r += smoothstep(0.02, 0.01, abs(a.y)) * 0.2;
    col.g += smoothstep(0.02, 0.01, abs(b.x)) * 0.2;
    col.g += smoothstep(0.02, 0.01, abs(b.y)) * 0.2;

    fragColor = vec4(col,1.0);
}
