// Fork of https://shadertoy.com/view/tslSDl

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U = fract( 4.* U/R.y * mat2(1.155,0,.5,1)); // hexa coords
    vec3 P = vec3(U,fract(U.x-U.y));            // local hexa coords
    O.rgb = P;
    P = abs(2.*P-1.);
    //O++;//O.rgb = .5+P-P;
    float M = max(P.x,max(P.y,P.z)),
          m = min(P.x,min(P.y,P.z));
    O -= vec4(M>.95);
    O -= vec4(m<.1);
    //O += float(abs(m-.33)<.05);
    O += sin(30.*m);

}
