#define smoothing 0.004 
vec3 rect(vec2 uv, vec2 wh)
{
    vec2 l = smoothstep(wh - smoothing, wh + smoothing, uv); 
    float c = l.x * l.y;
    vec2 r = smoothstep(wh - smoothing, wh + smoothing, 1.0 - uv);
    c *= r.x * r.y;
    return vec3(c);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy/iResolution.xy;
  	vec2 wh;
    vec2 border = vec2(0.005);

    vec3 outer;
    vec3 inner;
    vec3 c = vec3(1.0);

    float multiplier = 1.0 - abs(sin(iTime/7.));
    float d = smoothstep(0.1, 1.0, multiplier) * 2.5;
    multiplier += d;

    float move =  multiplier * 0.03 + 0.008;    

    for(float i = 0.; i < 45.; i++)
    {
	 wh = vec2(0.06 + move * i,0.01 + move * i);
     outer  = 1.0 - rect(uv, wh - border);
     inner = rect(uv, wh);
     c *= inner + outer;
    }   
     fragColor = vec4(c,1.0);
}
