float MaxIteration = 100.0f;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float oldRe, oldIm;
    vec2 uv = (fragCoord.xy/(iResolution.xy/2.5f)-1.25f);
	uv.x *= iResolution.x/iResolution.y;
    //uv.x -=0.746087f;
    //uv.y +=0.14948f;
    uv.x -=0.4f;
    vec2 c0 = uv;
    vec2 z = vec2(0.);

    float i =0.0f;;

    for ( i = 0.0f; i < MaxIteration; i++)
    {
        z = vec2(z.x * z.x - z.y * z.y, 2.0f * z.x * z.y) + c0;
        if (length(z) > 2.0f) break;
    }

    fragColor = vec4(vec3(i/MaxIteration)*vec3(0.0f,1.0f,0.4f),  1.0f);       
}
