void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float time = iTime;

    float x = uv.x;
    float y = uv.y;
    float scaleX = 5.;
    float scaleY = 1.;
    float offsetX = .5;
    float offsetY = 0.224;
    float taperX = 4.288;
    float taperY = 1.000;

    offsetX -= time;

    float tempX = x / (1. + (y * taperX));
    float tempY = y / (1. - (x * taperY));
    x = tempX;
    y = tempY;

    float res = 1.-abs(sin((x+offsetX)*3.14*scaleX));
    res *= scaleY;
    res += offsetY;
    float col = step(y,res);

    fragColor = vec4(col,col,0.,1.0);
}
