void mainImage(out vec4 O, vec2 C) {
	O-=O;
	for(float e,g=0.,i=0.;++i<50.;){
		vec3 p=g*vec3(C/iResolution.y-.7,1);
		p.z+=iTime*4.;
		p=mod(p,8.)-4.;
		p.xz=length(p.xz)*vec2(sin(iTime)*.5,2.);
		p.z=fract(p.z)-.5;
        for(int j=0;j++<3;)p.yz=abs(p.yz)-.1;
		g+=e=dot(abs(p),.5+p-p)-1.;
        e<.01?O.xyz+=abs(cos(p*9.+iTime))/i:p;
    }
}
