void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    int grayscale = 0;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 textura  = texture( iChannel0 , uv);

    //IF GRAYSCALE
    if (grayscale == 1)
    {
    textura.r =  (textura.r + textura.g + textura.b)/3.0;
    textura.b =  (textura.r + textura.g + textura.b)/3.0;
    textura.g =  (textura.r + textura.g + textura.b)/3.0;
    }


    float maxeduv = floor(uv.x*30000.);
    float maxeduvy = floor(uv.y*30000.);

    float contrast = 2.0;
    float brightness = 25.0;


    int uvxs = int(maxeduv);
    int uvxsy = int(maxeduvy);

    int uvx = uvxs % 4;
    int uvy = uvxsy % 8;

    vec3 outcolor = vec3(0,0,0)+textura.rgb;
    if(uvx == 1){
        outcolor.r = 1.0;
    	outcolor.g = 0.7;
    }
    else if(uvx == 2){
        outcolor.b = 1.0;
        outcolor.r = 0.7;
    }
    else if(uvx == 0){
        outcolor.g = 1.0;
        outcolor.b = 0.7;
    }


    if(uvy == 0 ){
        outcolor *= 0.9;
    }

    outcolor *= textura.rgb;

    //  Brightness
    outcolor += brightness/255.0;

    outcolor = outcolor - contrast * (outcolor -1.0) * outcolor * (outcolor-0.5);

    //Horizontal Lines
    float barstart = sin(300.0*iTime)*0.5+0.5;
    float barend = sin(300.0*iTime+0.02)*0.5+0.5;

    if (uv.x > barstart && uv.x < barend){
    	outcolor *= 0.9;
    }
    //Vertical Lines
    float barstart2 = sin(1.0*iTime)*0.5+0.5;
    float barend2 = sin(1.0*iTime+0.02)*0.5+0.5;


    if (uv.y > barstart2 && uv.y < barend2){
    	outcolor *= 0.9;
    }







    // Implemento Grain
    float strength = 16.0;


    //Sampled In Time
    float x = (uv.x + 4.0 ) * (uv.y + 4.0 ) * (iTime * 50.0);


	vec4 grain = vec4(mod((mod(x, 13.0) + 1.0) * (mod(x, 123.0) + 1.0), 0.01)-0.005) * strength;


    vec3 col = outcolor;


    fragColor = vec4(col,1.0)+grain;
}
