// Fork of https://shadertoy.com/view/Mty3Wh // 2018-12-30 00:55:16

float start=65.; //1...
float iMouseX_div=40.; //1...100
float iMouseY_div=1.; //1...100

void mainImage( out vec4 O, vec2 U )
{

    O = vec4( 
  mod(mix(U.x,U.y, 
      iMouse.y/iResolution.y/iMouseY_div)+U.y,
      sin(1.+iTime/(100.*(start+iMouse.x/iMouseX_div))))
   );


 if(texelFetch(iChannel1, ivec2(49,2),0).x ==1.) //keyboard 1 
    {
 O.g =
    mod(mix(U.x,U.y, 
      iMouse.y/iResolution.y/iMouseY_div)+U.y*1.001,
      sin(1.+iTime/(100.*(start+iMouse.x/iMouseX_div))))
     ;
 O.b =
       mod(mix(U.x,U.y, 
      iMouse.y/iResolution.y/iMouseY_div)+U.y,
      sin(1.+iTime/(100.*(start+iMouse.x*1.001/iMouseX_div))))
 ;
    }

 if ( U.y/iResolution.y > .5 ) O = sqrt(O);
}
