// Based on this tutorial: http://pixelshaders.com/examples/quasicrystal.html

float rotate(vec2 p, float angle) {
    vec2 direction = vec2(cos(angle), sin(angle));
    return cos(dot(p, direction));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;

    vec2 p = (st - 0.5) * 50.;

    float b = 0.;
	b += rotate(p, 0.);
    b += rotate(p, iTime);
    b += rotate(p, 1.);
    b += rotate(st, abs(sin(iTime+p.x) * 5.));

    b = 1.-pow(b, abs(sin(iTime)));

    // Output to screen
    fragColor.rgb = vec3(b);
    fragColor.a = 1.;
}
