float heart(vec2 st)
{
     st=(st-vec2(0.1,-0.3))*vec2(1.0,1.4);
     return pow(st.x,2.0)+pow(st.y-sqrt(abs(st.x)),2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p=(fragCoord.xy*2.0-iResolution.xy)/min(iResolution.x,iResolution.y);
	float d=heart(p);
    float c=smoothstep(0.5,3.*100./iResolution.y,abs(sin(d*9.0-iTime*2.0)));
    fragColor=vec4(c);
}
