vec2 convCoords(vec2 oldCoords)
{
    return (oldCoords - iResolution.xy * 0.5) / iResolution.yy * 5.0;
}

vec3 sphereColor = vec3(1.0, 0.0, 0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pixel = convCoords(fragCoord);
    vec2 mouse = convCoords(iMouse.xy);

    vec3 color;
    if (pixel.x * pixel.x + pixel.y * pixel.y < 1.0) {
        color = sphereColor;
    } else {
        color = vec3(1.0, 1.0, 0.0);
    }

    // Output to screen
    fragColor = vec4(color,1.0);
}
