void mainImage( out vec4 f, in vec2 u )
{
    f = texture(iChannel0, u/iResolution.xy);
}

const float pi = 3.1415926;
vec2 hash22(vec2 p) // Dave hoshkin hash
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

vec2 R;
vec4 T(vec2 p){
 	return texture(iChannel0, p / R);   

}
vec4 T2(vec2 p){
 	return texture(iChannel1, p / R);
}

void mainImage( out vec4 f, in vec2 u )
{
    R = iResolution.xy;

    vec2 uv = u/R.xy;

    vec4 col = vec4(0);

    col = T(u);

    vec4 me = T(u);

    vec4 Up = T(u + vec2(.0, 1.));
    vec4 Down = T(u + vec2(.0, -1.));
    vec4 Left = T(u + vec2(-1., .0));
    vec4 Right = T(u + vec2(1., .0));

    vec2 resDir = normalize(Up.yz + Down.yz + Left.yz + Right.yz);

    resDir += (hash22(u - sin(uv*25.))*.1);

    float angle = abs(atan(resDir.y, resDir.x)) - pi;

    u.x += 0.25;
    u.y += 0.3;

    float s = smoothstep(0.0, 1.0, angle);
    vec4 newCol = mix(T(u + (resDir - 1.0)), col, s);

    col = newCol;

    if(iFrame <= 5)
        col = T2(u);

    f = col;
}
