vec3 brown = vec3(0.3,0.2,0.2);
vec3 black = vec3(0.0,0.0,0.0);
vec3 red = vec3(0.7,0.0,0.0);
vec3 blue = vec3(0.,1.,1.);
vec3 yellow = vec3(1.0,1.0,0.0);
vec3 green = vec3(0.1,0.9,0.0);
vec3 white = vec3(1.0,1.0,1.0);

float blades(vec2 uv, vec2 pos, float r, float minPoint, float maxPoint, float val, float edgeCnt, float speed)
{   
    uv.x *= iResolution.x/iResolution.y;
    float dist = distance(pos, uv) * r;
    float value = step(r, dist);

    uv -= pos;

    float angle = (atan(uv.y, uv.x) + ( val * iTime) * speed);

    float radius = smoothstep(minPoint, maxPoint, cos(angle * edgeCnt)) * 0.2 + 0.5;

    value = step(radius , dist);

    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float mBlades = blades(uv, vec2(0.9,0.45), 7.0, -0.1, 2.0, 1.0, 3.0, 0.5);
    mBlades -= blades(uv, vec2(0.9,0.45), 5.5, -0.1, 2.0, -1.0, 10.0, 2.0);
    mBlades += blades(uv, vec2(0.9,0.45), 3.5, 0.5, 1.0, 1.0, 20.0, 25.0);
    mBlades -= blades(uv, vec2(0.9,0.45), 2.3, 1.0, 0.9, -1.0, 2.0, 0.1);
    mBlades += blades(uv, vec2(0.9,0.45), 1.9, 1., 0.9, 1.0, 5.0, 1.0);
    mBlades -= blades(uv, vec2(0.9,0.45), 1.5, 1., 0.9, 1.0, 10.0, 1.0);

    // Output to screen
    fragColor = vec4(vec3(mBlades),1.0);
}
