void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
	vec3 colora = vec3(0.);
    vec3 colorb = vec3(0.); 

    int cellx = int(floor(uv.x * 200.));
    int celly = int(floor(uv.y * 12.));

    bool foo = (celly % 2) != 0;

    float a = sin((iTime) + (float(cellx)));
    float b = sin((iTime) + (float(cellx)) + sin(iTime) * 2.);

    colora = vec3(0.,a, a);
    colorb = vec3(0., b, b);

    if(foo) {
    	fragColor = vec4(colora, 1.);
    } else {
    	fragColor = vec4(colorb, 1.);
    }
}
