float hash( vec2 p ) 
{
	float h = dot(p,vec2(127.1,311.7));	
	return fract(sin(h)*43758.5453123);
}

float noise( in vec2 p ) //[0~1]
{
	vec2 i = floor( p );
	vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
	return mix( mix( hash( i + vec2(0.0,0.0) ), 
		hash( i + vec2(1.0,0.0) ), u.x),
		mix( hash( i + vec2(0.0,1.0) ), 
		hash( i + vec2(1.0,1.0) ), u.x), u.y);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 outColor = texture(iChannel0,uv);



    float lumi = dot(outColor.rgb,vec3(0.30,0.59,0.11));
	float time = dot(uv,vec2(1,1))*100. + iTime*6.;
	time += lumi*20.;
	vec3 lightColor = vec3(0,0.8,1)*noise(vec2(time,0));



		float testWidth = 1.;
		vec4 sumColor = vec4(0.,0.,0.,0.);
		sumColor += texture(iChannel0,uv+vec2(-testWidth,-testWidth)/iResolution.xy);
		sumColor += texture(iChannel0,uv+vec2(-testWidth, testWidth)/iResolution.xy);
		sumColor += texture(iChannel0,uv+vec2( testWidth,-testWidth)/iResolution.xy);
		sumColor += texture(iChannel0,uv+vec2( testWidth, testWidth)/iResolution.xy);
		vec4 difColor = sumColor-outColor*4.;

		//outColor.rgb = outColor.rgb*0.5+lightColor*(1.- min(1.,length(difColor)*60.));
    outColor.rgb = outColor.rgb*0.5+lightColor*(min(1.,length(difColor)*2.8));

    //outColor.rgb = lightColor*(1.- min(1.,length(difColor)*60.));


    fragColor = outColor;
}
