float circle(in vec2 _st, in float _radius){
    vec2 dist = _st-vec2(0.5);
	return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(dist,dist)*4.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q = uv - vec2(.5 , 2.);


    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    vec3 color = vec3(sin(iTime*.5),0,tan(iTime));
    vec3 viewframe = vec3(circle(uv,cos(iTime)*q.y)*q.x) * tan(iTime)*tan(q.y/sin(uv.y)*iTime);

    /*
    vec3 viewframe = vec3(
        tan(sin(iTime)-cos(iTime))*tan(q.x / q.y)
                       );
	*/

   	vec3 screen = vec3(viewframe + color);

    // Output to screen
    fragColor = vec4(screen,1.0);
}
