float noise4(vec2 p) {
	return .5 * texture(iChannel2,p).r
        + .25 * texture(iChannel2,p+p).r
        + .125 * texture(iChannel2,4.*p).r
        + .6125 * texture(iChannel2,8.*p).r;
}

#define PI acos(-1.)
#define TAU (PI+PI)

void mainImage( out vec4 O, in vec2 u ) {
    vec2 R = iResolution.xy, p = (u+u-R)/R.y;
    float N = 7.;
    float t = .5*iTime/N;
    vec2 v = vec2(sin(N*t)*cos(N*t*TAU), cos(N*t)*sin(N*t*TAU));
    p -= .25*v;
    float z = length(p);
    float z1 = 1./z;

    float a = atan(p.x,p.y)/TAU+.5;

    vec2 co = vec2(a*8.-0.6*z1, z1+iTime);

    vec3 al = mix(texture(iChannel0, co).rgb,
                  texture(iChannel1, co).rgb,
                  pow(.5+.5*noise4(co*vec2(.02)),4.5));
    O.rgb = al * max(.85/(z+.33),0.)
        //+ al * pow(max(cos(a*6.28),0.),50.)
        ;
}
