void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x *= iResolution.x/iResolution.y;
    float time = sin(iTime) * 0.5;

    vec2 t = vec2(0.55 + time, 0.5);
    vec2 t2 = vec2(0.75 - time, 0.5);
    vec2 t3 = vec2(0.95 + time, 0.5);
    float d = length(uv - t);
    float d2 = length(uv - t2);
    float d3 = length(uv - t3);

    float r = smoothstep(0.45 + time, 0.1 + time, d);
    float g = smoothstep(0.45 + time, 0.1 + time, d2);
    float b = smoothstep(0.45 + time, 0.1 + time, d3);

    // Output to screen
    fragColor = vec4(r,g,b,1.0);
}
