// Original is 138 BPM
// Remixes are 160-170ish
// 138 / 60
#define MULT 2.3


// with max saturation and value
vec3 hue2rgb(in float c) {
    vec3 rgb = clamp( abs(mod(c*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return mix( vec3(1.0), rgb, 1.);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float hue = fract(iTime * MULT);
    vec3 color = hue2rgb(hue);
    fragColor = vec4(color, 1.0);
}
