void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    float full =mod(iTime,2.0);
    float iteration = floor(iTime/2.0);

    float freq = 1.0;


    vec2 res = (fragCoord.xy)/(iResolution.x);
	vec2 p = fract((res)*freq);
    vec2 p2 = fract((res)*freq);
    vec2 current = res;	

   vec4 color = vec4(0.0,0.0,0.0,1.0);





	vec2 circleCenter = vec2(0.5,0.5);
	float circleRadius_ =full*0.5;



	float dist = length(p - circleCenter );
	float dist2 = length(p - circleCenter )*2.0;




    float circle = smoothstep(circleRadius_, circleRadius_ - 0.15, dist);
    float circle2 = smoothstep(circleRadius_, circleRadius_ - 0.15, dist2*3.0);


	color = vec4(circle,circle,circle,1);
    ;

