vec4 image(vec2 p){
    //return vec4(cos((p.x/iResolution.x+vec4(0.,.33,.66,0.))*6.28)*.5 + .5)*(p.y/iResolution.y);
    return texture(iChannel0, p/iResolution.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iMouse.z>0.){
        fragColor = image(fragCoord);
    }else{
        fragColor = step(
            texelFetch(iChannel1, ivec2(mod(fragCoord, 8.0)), 0).rrrr, 
            pow(image(fragCoord),vec4(1.5))
        );
    }
}
