void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xx;

    float diff = length(uv - vec2(.5, .55));

    float gradient = 1.0 - mix(2.0, 0.0, uv.y + 0.5);

    float progress = smoothstep(0.45, .5, 1.0 - diff) * smoothstep(0.3, .7, diff);

    vec2 disturbedUV = vec2(uv.x, uv.y - iTime / 2.5) / 2.0;
    float noise = texture(iChannel0, disturbedUV).r;

    float pct = (progress - gradient) * noise;
    vec3 color = vec3(0./255., 250./255., 253./255.);

    fragColor = vec4(color * pct, 1.0);
}
