vec3 draw_rect(in vec2 st, in vec2 bottomleft, in float xl, in float yl,in vec3 color){
    	//draw shape
        vec2 leftBottom = step(vec2(0.0), st-bottomleft);
        vec2 rightTop = step(vec2(0.0),bottomleft+vec2(xl,yl)-st);
        vec3 recColor = vec3(leftBottom.x*leftBottom.y*rightTop.x*rightTop.y)*color;
        return recColor;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    // background
	vec3 col = vec3(0.0);

    col += draw_rect(uv, vec2(0.1),0.5,0.5,vec3(1.,0.,0.));
    col += draw_rect(uv, vec2(0.3),0.5,0.9,vec3(0.,1.,1.));
    col += draw_rect(uv, vec2(0.5,0.2),0.5,0.4,vec3(0.,0.,1.));
    col += draw_rect(uv, vec2(1.1,0.2),0.2,0.2,vec3(0.5,0.,1.));
    // Output to screen
    fragColor = vec4(col,1.0);
}
