////////////////////////////////////////////////////////////////////////////////
//
// Colourful Apollonian III, mla, 2020
//
// Another Apollonian Gasket - this is the same as the one in
// https://www.shadertoy.com/view/tsXBRf by sl2c, but constructed in a
// slightly different way that brings out the 3-fold symmetry. A
// notable feature is inversion in a circle of imaginary radius.
//
// <mouse>: apply inversion
// <up>/<down>: zoom in/out
// a,b,c,d,r,s,t: various controls for colouring
// m: mouse action
// x: show inversion circles
//
////////////////////////////////////////////////////////////////////////////////

const float PI = 3.1415927;

const int N = 3; // Any odd N >= 3 will work
const float D = 1.0/cos(PI/float(N)); // Centre distance of outer circles
const float R = D*sin(PI/float(N));   // Radius of outer circles
const float K = D-R;                  // Radius squared of centre circle.

vec3 getcircle(int i) {
  float t = 2.0*PI*float(i)/float(N);
  return vec3(D*vec2(sin(t),cos(t)),R*R);
}

vec3 pcolor(vec2 z) {
  int nsteps = 50;
  int step = 0;
  for (step = 0; step < nsteps; step++) {
    if (length(z) < K) break;
    float k = K/dot(z,z);
    z *= -k; // Imaginary radius
    for (int i = 0; i < N; i++) {
      vec3 c = getcircle(i);
      if (dot(z-c.xy,z-c.xy) < c.z) {
        z -= c.xy;
        z *= c.z/dot(z,z);
        z += c.xy;
        break;
      }
    }
  }
  if (step == nsteps) return vec3(0);
  vec3 col = vec3(1);
  if (key(CHAR_A)) col *= vec3(step%2);
  if (key(CHAR_B)) col *= vec3(1-step%2);
  if (!key(CHAR_C)) col *= hsv2rgb(vec3(float(step)/6.0,1,1));
  if (key(CHAR_D)) col *= (step%2==0) ? vec3(0,0,1): vec3(1,0,0);
  if (key(CHAR_R)) col = 1.0-col;
  if (key(CHAR_S)) col = mix(vec3(1),col,0.5);
  if (!key(CHAR_T)) col *= 1.0-length(z)/K;
  return col;
}

vec3 getcolor(vec2 z, vec2 w) {
  if (!key(CHAR_M) && iMouse.x > 0.0) {
    z = invertto(z,w);
  } else if (true) {
    float k = 1.618*sin(0.5*iTime);
    z.y += k;
    z = invert(z,vec3(0,0,1.0));
    z = invert(z,vec3(0,k,1));
  }
  vec3 color = pcolor(z);
  if (key(CHAR_X)) {
    float lwidth = 0.01;
    float pwidth = 2.0*fwidth(z.x);
    vec3 ccolor = 0.8*vec3(1,1,0);
    color = mix(ccolor,color,smoothstep(0.5*lwidth,lwidth,abs(length(z)- sqrt(abs(K)))));
    for (int i = 0; i < N; i++) {
      vec3 c = getcircle(i);
      color = mix(ccolor,color,smoothstep(0.5*lwidth,lwidth,abs(length(z-c.xy)-sqrt(c.z))));
    }
  }
  return color;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  float AA = 2.0;
  vec3 color = vec3(0);
  float scale = 1.0;
  scale *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 z = (2.0*(fragCoord+vec2(i,j)/AA)-iResolution.xy)/iResolution.y;
      vec2 w = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
      z *= scale; //w *= scale;
      color += getcolor(z,w);
    }
  }
  color /= AA*AA;
  color = pow(color,vec3(0.4545));
  fragColor = vec4(color,1.0);
}

vec2 invert(vec2 z, vec3 c) {
  z -= c.xy;
  z *= c.z/dot(z,z);
  z += c.xy;
  return z;
}

vec2 invertto(vec2 z, vec2 m) {
  float X = dot(m,m);
  m /= X;
  return invert(z,vec3(m,(1.0-X)/X));
}

vec3 hsv2rgb(in vec3 c) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

#define key(code) (texelFetch(iChannel3, ivec2(code,2),0).x != 0.0)

#define store(i,j) (texelFetch(iChannel2, ivec2(i,j),0))

#define keycount(key) (int(store(0,key).x))

const int CHAR_A = 65;
const int CHAR_B = 66;
const int CHAR_C = 67;
const int CHAR_D = 68;
const int CHAR_H = 72;
const int CHAR_J = 74;
const int CHAR_L = 76;
const int CHAR_M = 77;
const int CHAR_N = 78;
const int CHAR_R = 82;
const int CHAR_S = 83;
const int CHAR_T = 84;
const int CHAR_U = 85;
const int CHAR_V = 86;
const int CHAR_W = 87;
const int CHAR_X = 88;
const int CHAR_Y = 89;
const int CHAR_Z = 90;
const int KEY_LEFT = 37;
const int KEY_RIGHT = 39;
const int KEY_UP = 38;
const int KEY_DOWN = 40;

#define keystate(k,n) ( texelFetch(iChannel3,ivec2(k,n),0) )

void mainImage(out vec4 t, vec2 uv) {
  int i = int(uv.x);
  int j = int(uv.y);
  if ( iFrame == 0) {
    t = vec4(0);
  } else {
    t = texelFetch(iChannel2,ivec2(i,j),0); // Get current state   
  }  
  if (i == 0) {
    float w1 = keystate(j,2).x;
    if (w1 != t.w) {
      t.w = w1;
      t.x++;
    }
  }     
}
