const float PI = 3.14159265;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    for (int i=0; i<3; i++) {
    	p = abs(p)/dot(p,p);
        p -= vec2(sin(iTime*0.1347), cos(iTime*0.1473));
    }

    vec4 s1 = vec4(1.0, 1.0, 7.0, 2.0);
    vec4 s2 = vec4(4.0, 4.0, 2.0, 4.6);

    float tx = sin(iTime)*0.2347; 
    float ty = cos(iTime)*0.2473; 

    vec4 s = mix(s1, s2, vec4(tx,tx,ty,ty));

    float amp = 
        s.x * sin(PI*s.z*p.x) * sin(PI*s.w*p.y) + 
        s.y * sin(PI*s.w*p.x) * sin(PI*s.z*p.y);

    fragColor = vec4(1.0 - smoothstep(abs(amp), 0.0, 0.1));
}
