void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord.xy - .5*R)/R.y;
    vec2 m = (iMouse.xy - .5*R)/R.y;
    float ret = 1.;

    float t = iTime;
    uv = (uv-m)/exp2(t) + m;

    vec2 z = vec2(0);
    float N = 10.*t;
    N = floor(N);
    N = min(N, 1023.);

    float iter;
    for(iter=0.; iter<N; iter++)
    {
        z = mat2(z, -z.y, z.x)*z + uv;
        if(dot(z, z) > 4.)
            break;
    }

    ret = iter/N;

    fragColor = vec4(ret);
}
