const float PI = 3.14159265;
const float deviation = 1./3.;

float rad (in float deg) {
	return PI / 180. * deg;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(step(abs(uv.y-0.5-sin(uv.x*3.14*4.-iTime)/2.),deviation),step(abs(uv.y-0.5-sin(uv.x*3.14*4.-(iTime+rad(120.)))/2.),deviation),step(abs(uv.y-0.5-sin(uv.x*3.14*4.-(iTime+rad(240.)))/2.),deviation));

    // Output to screen
    fragColor = vec4(col,1.0);
}
