void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float k=iResolution.x/4.0;
    int i;
    vec2 xy = fragCoord.xy;
    vec2 cxy0 = vec2((xy.x-iResolution.x/2.0)/k,(xy.y-iResolution.x/3.5)/k);
    vec2 cxy = cxy0;
    vec2 cxy1 = cxy;
    cxy0[1]=(iMouse.x-iResolution.x/2.0)/iResolution.x*2.0;
    cxy0[0]=(iMouse.y-iResolution.y/2.0)/iResolution.y*2.0;
    xy.x=cxy1[0]*cxy1[0];
    xy.y=cxy1[1]*cxy1[1];
    for (i=0;i<3000 && (xy.x+xy.y<4.0);i++)
    {
       cxy[0]=xy.x-xy.y+cxy0[0]; 
       cxy[1]=2.0*cxy1[0]*cxy1[1]+cxy0[1];
       cxy1=cxy;
       xy.x=cxy1[0]*cxy1[0];
       xy.y=cxy1[1]*cxy1[1];
    }
    fragColor = vec4(sin(float(i)),cos(float(i)/3.141592),sin(float(i)/3.141592),0.0);
    //fragColor = vec4(float(xy.x+xy.y<4.0)*cos(xy.x+xy.y),cos(xy.x),sin(xy.y),1.0);   *cos(3.141592*cos(xy.x+xy.y))
}
